mapper.select:
SELECT board_id, title, content, hit, created_at, updated_at, user_id, visible
FROM board
WHERE board_id = #{boardId}

mapper.update:
UPDATE board
SET title = #{title}, content = #{content}, updated_at = NOW(), visible = #{visible}
WHERE board_id = #{boardId}

UPDATE board
SET hit = hit + 1
WHERE board_id = #{boardId}

UPDATE board
SET likes = likes + 1
WHERE board_id = #{boardId}
